// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

interface IGinixCoreLevel {
    function getPlayerLevel(address player) external view returns (uint32);
}

/**
 * @title GinixGameRegistry
 * @notice Registry for approved games in Ginix Arcade
 * @dev Only approved games can grant rewards
 */
contract GinixGameRegistry is Ownable {
    struct GameMeta {
        bool approved;
        string uri; // metadata JSON (name, icon, description, etc)
        uint64 addedAt;
        uint32 minLevel; // minimum player level required (0 = no restriction)
    }

    IGinixCoreLevel public coreContract;

    mapping(bytes32 => GameMeta) public games;
    bytes32[] public gameIds;

    event GameApproved(bytes32 indexed gameId, string uri, uint32 minLevel);
    event GameRevoked(bytes32 indexed gameId);
    event GameURIUpdated(bytes32 indexed gameId, string newUri);
    event GameMinLevelUpdated(bytes32 indexed gameId, uint32 newMinLevel);
    event CoreContractUpdated(address newCoreContract);

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Convert game slug to gameId
     * @param slug Game identifier (e.g., "neon-sky-runner")
     */
    function toGameId(string memory slug) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(slug));
    }

    /**
     * @notice Set the GameCore contract address for level checks
     * @param _core Address of the GinixGameCore contract
     */
    function setCoreContract(address _core) external onlyOwner {
        require(_core != address(0), "Invalid core address");
        coreContract = IGinixCoreLevel(_core);
        emit CoreContractUpdated(_core);
    }

    /**
     * @notice Approve a new game
     * @param gameId Unique game identifier
     * @param uri Metadata URI
     */
    function approveGame(bytes32 gameId, string calldata uri) external onlyOwner {
        require(!games[gameId].approved, "Game already approved");
        require(bytes(uri).length > 0, "URI cannot be empty");

        games[gameId] = GameMeta({
            approved: true,
            uri: uri,
            addedAt: uint64(block.timestamp),
            minLevel: 0
        });

        gameIds.push(gameId);
        emit GameApproved(gameId, uri, 0);
    }

    /**
     * @notice Approve a new game with a minimum level requirement
     * @param gameId Unique game identifier
     * @param uri Metadata URI
     * @param minLevel Minimum player level required to play
     */
    function approveGameWithLevel(bytes32 gameId, string calldata uri, uint32 minLevel) external onlyOwner {
        require(!games[gameId].approved, "Game already approved");
        require(bytes(uri).length > 0, "URI cannot be empty");

        games[gameId] = GameMeta({
            approved: true,
            uri: uri,
            addedAt: uint64(block.timestamp),
            minLevel: minLevel
        });

        gameIds.push(gameId);
        emit GameApproved(gameId, uri, minLevel);
    }

    /**
     * @notice Update minimum level for a game
     * @param gameId Game to update
     * @param minLevel New minimum level
     */
    function setGameMinLevel(bytes32 gameId, uint32 minLevel) external onlyOwner {
        require(games[gameId].approved, "Game not approved");
        games[gameId].minLevel = minLevel;
        emit GameMinLevelUpdated(gameId, minLevel);
    }

    /**
     * @notice Check if a player can access a game (approved + meets level requirement)
     * @param gameId Game to check
     * @param player Player address
     */
    function canPlayerAccess(bytes32 gameId, address player) external view returns (bool) {
        if (!games[gameId].approved) return false;
        if (games[gameId].minLevel == 0) return true;
        if (address(coreContract) == address(0)) return true;
        return coreContract.getPlayerLevel(player) >= games[gameId].minLevel;
    }

    /**
     * @notice Revoke game approval
     * @param gameId Game to revoke
     */
    function revokeGame(bytes32 gameId) external onlyOwner {
        require(games[gameId].approved, "Game not approved");
        games[gameId].approved = false;
        emit GameRevoked(gameId);
    }

    /**
     * @notice Update game metadata URI
     * @param gameId Game to update
     * @param newUri New metadata URI
     */
    function updateGameURI(bytes32 gameId, string calldata newUri) external onlyOwner {
        require(games[gameId].approved, "Game not approved");
        require(bytes(newUri).length > 0, "URI cannot be empty");
        games[gameId].uri = newUri;
        emit GameURIUpdated(gameId, newUri);
    }

    /**
     * @notice Check if game is approved
     * @param gameId Game to check
     */
    function isApproved(bytes32 gameId) external view returns (bool) {
        return games[gameId].approved;
    }

    /**
     * @notice Get minimum level for a game
     * @param gameId Game to check
     */
    function getMinLevel(bytes32 gameId) external view returns (uint32) {
        return games[gameId].minLevel;
    }

    /**
     * @notice Get total number of approved games
     */
    function getGameCount() external view returns (uint256) {
        return gameIds.length;
    }

    /**
     * @notice Get game metadata
     * @param gameId Game to query
     */
    function getGameMeta(bytes32 gameId) external view returns (GameMeta memory) {
        return games[gameId];
    }
}

core/GinixRewardEngine.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

interface IGinixRegistry {
    function isApproved(bytes32 gameId) external view returns (bool);
}

interface IGinixGuard {
    function verifyAndConsume(
        bytes32 nonce,
        address player,
        bytes32 gameId,
        uint64 score,
        bytes calldata signature
    ) external returns (bool);
}

interface IGinixCore {
    function addXP(address player, uint64 amount) external;
    function recordSession(address player, bytes32 gameId, uint64 score, uint64 duration) external;
    function configureMultipliers(uint32[] calldata thresholds, uint32[] calldata values) external;
}

interface IGinixMemory {
    function unlock(address player, bytes32 achievementId, string calldata metadata) external;
}

/**
 * @title GinixRewardEngine
 * @notice Orchestrates reward distribution for game achievements
 * @dev Validates through AntiCheatGuard, then grants rewards
 */
contract GinixRewardEngine is Ownable {
    IGinixRegistry public registry;
    IGinixGuard public guard;
    IGinixCore public core;
    IGinixMemory public gameMemory;

    struct RewardConfig {
        uint64 xpAmount;
        bool unlockAchievement;
        bool enabled;
    }

    // rewardId => config
    mapping(bytes32 => RewardConfig) public rewards;

    event RewardGranted(
        address indexed player,
        bytes32 indexed rewardId,
        bytes32 indexed gameId,
        uint64 score,
        uint64 xpAwarded
    );
    event RewardConfigured(bytes32 indexed rewardId, uint64 xpAmount, bool unlockAchievement);
    event ContractsUpdated(address registry, address guard, address core, address gameMemory);

    constructor(
        address _registry,
        address _guard,
        address _core,
        address _memory
    ) Ownable(msg.sender) {
        require(_registry != address(0), "Invalid registry");
        require(_guard != address(0), "Invalid guard");
        require(_core != address(0), "Invalid core");
        require(_memory != address(0), "Invalid memory");

        registry = IGinixRegistry(_registry);
        guard = IGinixGuard(_guard);
        core = IGinixCore(_core);
        gameMemory = IGinixMemory(_memory);
    }

    /**
     * @notice Configure a reward
     * @param rewardId Reward identifier
     * @param xpAmount XP to award
     * @param unlockAchievement Whether to unlock achievement
     */
    function configureReward(
        bytes32 rewardId,
        uint64 xpAmount,
        bool unlockAchievement
    ) external onlyOwner {
        rewards[rewardId] = RewardConfig({
            xpAmount: xpAmount,
            unlockAchievement: unlockAchievement,
            enabled: true
        });
        emit RewardConfigured(rewardId, xpAmount, unlockAchievement);
    }

    /**
     * @notice Grant reward to player
     * @param nonce Session nonce
     * @param player Player address
     * @param gameId Game identifier
     * @param score Session score
     * @param duration Session duration
     * @param rewardId Reward to grant
     * @param backendSig Backend signature
     */
    function grantReward(
        bytes32 nonce,
        address player,
        bytes32 gameId,
        uint64 score,
        uint64 duration,
        bytes32 rewardId,
        bytes calldata backendSig
    ) external {
        // Validate game is approved
        require(registry.isApproved(gameId), "Game not approved");

        // Validate reward is configured
        RewardConfig memory reward = rewards[rewardId];
        require(reward.enabled, "Reward not enabled");

        // Verify signature and consume nonce
        require(
            guard.verifyAndConsume(nonce, player, gameId, score, backendSig),
            "Guard verification failed"
        );

        // Record session
        core.recordSession(player, gameId, score, duration);

        // Award XP
        if (reward.xpAmount > 0) {
            core.addXP(player, reward.xpAmount);
        }

        // Unlock achievement
        if (reward.unlockAchievement) {
            string memory metadata = string(
                abi.encodePacked(
                    "Game: ",
                    gameId,
                    ", Score: ",
                    uint2str(score)
                )
            );
            gameMemory.unlock(player, rewardId, metadata);
        }

        emit RewardGranted(player, rewardId, gameId, score, reward.xpAmount);
    }

    /**
     * @notice Update contract addresses (in case of upgrades)
     * @param _registry New registry address
     * @param _guard New guard address
     * @param _core New core address
     * @param _memory New memory address
     */
    function updateContracts(
        address _registry,
        address _guard,
        address _core,
        address _memory
    ) external onlyOwner {
        require(_registry != address(0), "Invalid registry");
        require(_guard != address(0), "Invalid guard");
        require(_core != address(0), "Invalid core");
        require(_memory != address(0), "Invalid memory");

        registry = IGinixRegistry(_registry);
        guard = IGinixGuard(_guard);
        core = IGinixCore(_core);
        gameMemory = IGinixMemory(_memory);

        emit ContractsUpdated(_registry, _guard, _core, _memory);
    }

    /**
     * @notice Disable a reward
     * @param rewardId Reward to disable
     */
    function disableReward(bytes32 rewardId) external onlyOwner {
        rewards[rewardId].enabled = false;
    }

    /**
     * @notice Proxy to configure XP multiplier tiers on GameCore
     * @dev Required because RewardEngine is the owner of GameCore,
     *      so only RewardEngine can call GameCore.configureMultipliers()
     * @param thresholds Sorted array of level thresholds
     * @param values Multiplier values in basis points (10000 = 1.0x)
     */
    function configureMultipliers(
        uint32[] calldata thresholds,
        uint32[] calldata values
    ) external onlyOwner {
        core.configureMultipliers(thresholds, values);
    }

    /**
     * @notice Helper to convert uint to string
     */
    function uint2str(uint256 _i) internal pure returns (string memory) {
        if (_i == 0) {
            return "0";
        }
        uint256 j = _i;
        uint256 len;
        while (j != 0) {
            len++;
            j /= 10;
        }
        bytes memory bstr = new bytes(len);
        uint256 k = len;
        while (_i != 0) {
            k = k - 1;
            uint8 temp = (48 + uint8(_i - _i / 10 * 10));
            bytes1 b1 = bytes1(temp);
            bstr[k] = b1;
            _i /= 10;
        }
        return string(bstr);
    }
}

